using System;
using System.IO;
using System.Globalization;
using System.Text;
using System.Windows.Forms;
using Google.Cloud.BigQuery.V2;
using Google.Apis.Bigquery.v2.Data;

public static class Snippet
{
	public static void func(System.String filePath, System.String myprojectid, System.String mydatasetid, System.String mytableid)
	{
		string projectId = myprojectid;
	    string datasetId = mydatasetid;
        string tableId = mytableid;
		BigQueryClient client = null;
		BigQueryJob job = null;
		BigQueryTable table = null;
		TableSchema schema = null;
		client = BigQueryClient.Create(projectId);
		var uploadJsonOptions = new UploadJsonOptions{ Autodetect=true };
		MemoryStream ms = new MemoryStream();



			using (StreamWriter writer = new StreamWriter(ms, new UTF8Encoding(false)))

		{   
			foreach (string line in File.ReadLines(filePath))
			{
				writer.WriteLine(line);
			}
			writer.Flush();
			ms.Seek(0,SeekOrigin.Begin);

			job = client.UploadJson(datasetId, tableId, schema, ms);
            job.PollUntilCompleted();
            table = client.GetTable(datasetId, tableId);
		}            
		}
}
