SQL_STMT_U.val:
declare @lfdnr int;
declare @anzahl int;
set @anzahl=(select count(*) from DOWNLOAD_BEREICHE where ebene1=:1 and ebene2=:2 and ebene3=:3 and ebene4=:4);
set @lfdnr=1;

WHILE @lfdnr<=@anzahl
	BEGIN
	update top(1) DOWNLOAD_BEREICHE set lfdnummer=@lfdnr,KompletterBereich=DownloadBereich where lfdnummerparent=0 and lfdnummer=0 and ebene1=:1 and ebene2=:2 and ebene3=:3 and ebene4=:4
	SET @lfdnr=@lfdnr+1
	END
