SnippetCode.val:
using System;
using System.Windows.Forms;
using System.Drawing;

public static class Snippet
{
	public static System.String func(System.String myinitialcolor, System.Int16 mycolora, System.Int16 mycolorr, System.Int16 mycolorg, System.Int16 mycolorb)
	{
		ColorDialog mycolordlg = new ColorDialog();

		    int[] lastcolors = new int[] {ColorTranslator.ToOle(Color.FromArgb(mycolora,mycolorr,mycolorg,mycolorb))};

			if (myinitialcolor != "")
			{			
				mycolordlg.CustomColors = lastcolors;
				mycolordlg.Color = Color.FromArgb(mycolora,mycolorr,mycolorg,mycolorb);
			}

			if (mycolordlg.ShowDialog() == DialogResult.OK) 
			{			
				return mycolordlg.Color.A.ToString()+";"+mycolordlg.Color.R.ToString()+";"+mycolordlg.Color.G.ToString()+";"+mycolordlg.Color.B.ToString();
			}
			else
			{
				return "";
			}

	}

}

