SnippetCode.val:
using System;
using System.Drawing;
using System.Text;
using TECIT.TBarCode;

public static class Snippet
{
	public static System.String CreateQRCode(System.String QRC_Text, System.String QRC_File, System.Int16 myBarcodeSize)
	{	Barcode myqrcode = new Barcode();
		myqrcode.BarcodeType = BarcodeType.QRCode ;
		myqrcode.Data = QRC_Text ;

		Rectangle qrc_rectangle;
		qrc_rectangle = new Rectangle (120,120,myBarcodeSize,myBarcodeSize);
		myqrcode.BoundingRectangle = qrc_rectangle;
		myqrcode.Draw (QRC_File,ImageType.Bmp);

		return "";
	}

}
